var hreq= require('httpreq');
var db=require('usrdb');

exports.cliGet=function(email,pid,sec){

var obj
var url= "https://api.paidy.com/payments/"+pid
const opt= {
headers:{ "Content-Type": "application/json" ,
"Paidy-Version": "2016-07-01",
"Authorization": "Bearer "+sec}
};

hreq.get(url,opt,  function(err,res){
if (err) { return console.log(err); }
else{
var obj=JSON.parse(res.body)

var mnt=obj.amount
var buy=obj.buyer
var str_buy=JSON.stringify(buy)

if(pid){
var ite=obj.order.items
var str_ite=JSON.stringify(ite)

var selpid=db.selPid(pid)
if(selpid==null){
db.insPid(email,pid,mnt,str_buy,str_ite)
}else{
console.log("pid exists")
}
}else{console.log("no pid")}

}
})
}

