var nodemailer = require("nodemailer");
var smtpTransport = require('nodemailer-smtp-transport');

exports.trEma=function(hos,usr,pss,ema,toe,mes){
var trans = nodemailer.createTransport(smtpTransport({
    host: hos,port: 465,
//    tls: true,
auth: {user: usr,pass:pss}
}));
console.log(trans.transporter.options)

try{
trans.sendMail({
from: ema,
to: toe,
subject: "loc:cli" ,
html:mes 
}) 
}catch(err){console.dir(err);}

}
