var nodemailer = require("nodemailer");

exports.trEma=function(hos,usr,pss,fre,toe,cc,sub,mes){
var trans = nodemailer.createTransport({
    host: hos,port: 465,
    tls: true,
auth: {user: usr,pass:pss}
});
console.log(trans.transporter.options)

try{
trans.sendMail({
from: fre,
to: toe,
cc:cc,
subject:sub, 
html:mes 
}) 
}catch(err){console.dir(err);}

}
